import React, { useCallback, useEffect, useState } from "react";

import InfiniteScroll from "react-infinite-scroller";
import { Box, CircularProgress, Container, Paper, TableContainer } from "@mui/material";

import { Modal } from "./components/Modal";
import { UsersData } from "./components/UsersData/UsersData";
import { ModalContext } from "./modal-context";

import { dataByPage } from "./api/constants/const";
import { BASE_URL, client } from "./api/fetch";

import { User } from "./types/User";
import "./index.scss";

export const App: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [, setError] = useState("");
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(false);

  const [modalContextValue, setModalContextValue] = useState<User>();
  const [isModalOpen, setIsModalOpen] = useState<boolean>(false);
  const [isCreate, setIsCreate] = useState<boolean>(false);

  const addUser = useCallback((user: User) => {
    setUsers((prevUsers) => [user, ...prevUsers]);
  }, []);

  const loadMoreUsers = async () => {
    const res = await client.get<User>(`?_page=${page + 1}&_limit=${dataByPage}`);

    await setUsers((prevList) => {
      return [...prevList, ...res];
    });

    if (!res) {
      setError("error");
    } else {
      setPage((prev) => prev + 1);
    }

    //   .then((res) => {
    //     setUsers((prevList) => [...prevList, ...res]);
    //   })
    //   .catch((e) => setError(e))
    //   .finally(() => setPage((prev) => prev + 1));
  };

  useEffect(() => {
    fetch(`${BASE_URL}?_page=${1}&_limit=${dataByPage}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`error with db ${response.statusText}`);
        }

        const total = Number(response.headers.get("X-Total-Count"));

        setHasMore(total > page * dataByPage);
      })
      .catch((e) => setError(e));
  }, [page]);

  return (
    <Container maxWidth="md" sx={{ pb: "20px" }}>
      <ModalContext.Provider
        value={{
          create: isCreate,
          open: isModalOpen,
          setCreate: setIsCreate,
          setItem: setModalContextValue,
          setOpen: setIsModalOpen,
          addUser: addUser,
        }}
      >
        <InfiniteScroll
          pageStart={1}
          loadMore={loadMoreUsers}
          hasMore={hasMore}
          loader={
            <Box
              sx={{
                display: "flex",
                margin: "20px",
                alignItems: "center",
                justifyContent: "center",
              }}
              key={`${page * dataByPage}`}
            >
              <CircularProgress />
            </Box>
          }
        >
          <TableContainer component={Paper} elevation={3}>
            <TableBody>
              <UsersData users={users} />
            </TableBody>
          </TableContainer>
        </InfiniteScroll>
        {isModalOpen ? <Modal /> : null}
      </ModalContext.Provider>
    </Container>
  );
};
